// Helper functions
export function getJokesByCategory(category: JokeCategory): LibraryJoke[] {
  return JOKE_LIBRARY.filter(joke => joke.category === category);
}

export function getRandomJokeFromCategory(category: JokeCategory): LibraryJoke | null {
  const jokes = getJokesByCategory(category);
  if (jokes.length === 0) return null;
  return jokes[Math.floor(Math.random() * jokes.length)];
}

export function getRandomJokeFromLibrary(): LibraryJoke {
  return JOKE_LIBRARY[Math.floor(Math.random() * JOKE_LIBRARY.length)];
}

export function getMultipleRandomJokes(count: number, category?: JokeCategory): LibraryJoke[] {
  const source = category ? getJokesByCategory(category) : JOKE_LIBRARY;
  const shuffled = [...source].sort(() => Math.random() - 0.5);
  return shuffled.slice(0, Math.min(count, shuffled.length));
}

export function searchJokes(query: string): LibraryJoke[] {
  const lowerQuery = query.toLowerCase();
  return JOKE_LIBRARY.filter(joke =>
    joke.joke.toLowerCase().includes(lowerQuery)
  );
}

export function getTopRatedJokes(count: number = 10): LibraryJoke[] {
  return [...JOKE_LIBRARY]
    .sort((a, b) => b.rating - a.rating)
    .slice(0, count);
}

export function getAllCategories(): JokeCategory[] {
  return Object.keys(CATEGORY_INFO) as JokeCategory[];
}

export function getCategoryStats(): Record<JokeCategory, number> {
  const stats: Partial<Record<JokeCategory, number>> = {};
  for (const joke of JOKE_LIBRARY) {
    stats[joke.category] = (stats[joke.category] || 0) + 1;
  }
  return stats as Record<JokeCategory, number>;
}

export const TOTAL_JOKES = JOKE_LIBRARY.length;
export const TOTAL_CATEGORIES = Object.keys(CATEGORY_INFO).length;
